import React, {useState} from "react";
import { Text, StyleSheet, View, TextInput } from "react-native";



const InputText = () => {
        const [name, setName] = useState ('Password must be longer than 5 Characters');
        const [counter, setCounter] = useState(0);

    return( 
    
    <View>
        <Text>Enter Password:</Text>
        <TextInput 
        style = {styles.input}
        autoCapitalize = "none"
        autoCorrect = {false}
        onChangeText = {() => setCounter(counter + 1) }
        />

        { counter < 5 && counter > 0 ?  (NewValue) => setName (NewValue)  :  null}
        
        <Text>{NewValue}</Text>
    </View>
    
    );


};

const styles = StyleSheet.create({

    input: {

        margin: 15,
        borderColor: 'black',
        borderWidth: 1
    }

}) ;


export default InputText;
