import React, {useState} from 'react';
import { Text, StyleSheet, Button, View, FlatList } from 'react-native';
import ColorCounter from '../components/ColorCounter';


const ColorAdjustScreen = () => {

const [red, setRed] = useState(0);
const [green, setGreen] = useState(0);
const [blue, setBlue] = useState(0);


return (
    <View>

        <ColorCounter onIncrease = {() => setRed(red+1)} onDecrease = {() => setRed(red-1)} color = "Red"/>
        <ColorCounter onIncrease = {() => setGreen(green+1)} onDecrease = {() => setGreen(green-1)}  color = "Blue"/>
        <ColorCounter onIncrease = {() => setBlue(blue+1)} onDecrease = {() => setBlue(blue-1)}  color = "Green"/>

        <View style = {{height:100, width:100, backgroundColor: `rgb(${red}, ${green}, ${blue} )` }}></View>

    </View>

);

};





const style = StyleSheet.create ({


});

export default ColorAdjustScreen;
